def solve(A):
    count=0
    max_index=0
    min_value=0
    i=0
    while i<len(A):
        while A[i]!=min_value:
            i=A[i]
            max_index=max(max_index,i)
        if min_value==i:
            i+=1
            count+=1
        else:
            i=max_index+1
            count+=1
            min_value=i
    return count
